<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'layout_name'			=> $_POST['layout_name'],					'house_id'			=> $_POST['house_id'],					'room_id'			=> $_POST['room_id'],					'properties'			=> $_POST['properties'],					'features'			=> $_POST['features'],					'flat_desc'			=> $_POST['flat_desc'],										'block'			=> $_POST['block'][0],					'created' => date('Y-m-d H:i:s', time()),					'modified' => date('Y-m-d H:i:s', time())	);					if ($_POST["layout_name"]) {		if ($_POST["house_id"]) {			if ($_POST["room_id"]) {				$query = "INSERT INTO [pre]$appTable ";						$fieldsStr = " ( ";								$valuesStr = " ( ";						$cntUpd = 0;				foreach($cardUpd as $field => $itemUpd)				{					$cntUpd++;										$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");										$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");				}								$fieldsStr .= " ) ";								$valuesStr .= " ) ";								$query .= $fieldsStr." VALUES ".$valuesStr;				$item_id = $ah->rs($query,0,1,1);				if($item_id)				{					$data['item_id'] = $item_id;				}else				{					$data['item_id'] = 0;				}				$data['real_item_id'] = $item_id;				$file_path = "../../../../split/files/layouts/";				$filename = "layouts";								if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)				{							$files_upload = $ah->mtvc_add_files_file_miltiple(array(							'path'			=>$file_path,							'name'			=>5,							'pre'			=>"flyts_",							'size'			=>10,							'rule'			=>0,							'max_w'			=>2500,							'max_h'			=>2500,							'files'			=>$filename,							'resize_path'	=>$file_path."crop/",							'resize_w'		=>500,							'resize_h'		=>350,							'resize_path_2'	=>"0",							'resize_w_2'	=>0,							'resize_h_2'	=>0						  ));					if($files_upload){						$now = date("Y-m-d H:i:s");						foreach($files_upload as $file_upload){							$query = "INSERT INTO `osc_sys_flats_layouts` (`filename`, `flat_id`, `created`, `modified`) VALUES ('$file_upload', '$item_id', '$now', '$now')";							$ah->rs($query);							chmod($file_path.$file_upload,0777);						}					}				}				$data['status'] = "success";				$data['message'] = "Квартира успешно сохранена.";			}else{				$data['message'] = "Выберите категорию.";			}		}else{			$data['message'] = "Выберите дом.";		}	}else{		$data["message"] = "Укажите название планировки.";	}					