<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_home_env_items";		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'caption'			=> $_POST['caption'],					'ref'			=> $_POST['ref'],					'time_walk'			=> (int)$_POST['time_walk'],					'time_car'			=> $_POST['time_car'],					'order_id'			=> (int)$_POST['order_id'],					'block'			=> $_POST['block'][0],					'modified' => date('Y-m-d H:i:s', time()),					'created' => date('Y-m-d H:i:s', time())	);						$query = "INSERT INTO $appTable ";				$fieldsStr = " ( ";								$valuesStr = " ( ";						$cntUpd = 0;				foreach($cardUpd as $field => $itemUpd)				{					$cntUpd++;										$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");										$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");				}								$fieldsStr .= " ) ";								$valuesStr .= " ) ";								$query .= $fieldsStr." VALUES ".$valuesStr;					$item_id = $ah->rs($query,0,1,1);				if($item_id)				{					$data['item_id'] = $item_id;				}else				{					$data['item_id'] = 0;				}				$data['real_item_id'] = $item_id;				$data['status'] = "success";				$data['message'] = "Элемент окружения успешно сохранен.";