<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'name'			=> $_POST['name'],					'alias'			=> $_POST['alias'],					'created' => date('Y-m-d H:i:s', time()),					'modified' => date('Y-m-d H:i:s', time())	);				$alias = $_POST["alias"];	if ($alias) {		$q = "SELECT M.id FROM `osc_page_news_cats` AS M WHERE M.alias = '$alias' LIMIT 1";		$check_alias = $ah->rs($q);		if (!$check_alias) {				$query = "INSERT INTO `osc_page_news_cats` ";				$fieldsStr = " ( ";								$valuesStr = " ( ";						$cntUpd = 0;				foreach($cardUpd as $field => $itemUpd)				{					$cntUpd++;										$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");										$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");				}								$fieldsStr .= " ) ";								$valuesStr .= " ) ";								$query .= $fieldsStr." VALUES ".$valuesStr;					$item_id = $ah->rs($query,0,1,1);				if($item_id)				{					$data['item_id'] = $item_id;				}else				{					$data['item_id'] = 0;				}				$data['real_item_id'] = $item_id;				$data['status'] = "success";				$data['message'] = "Рубрика успешно сохранена.";		}else{			$data["message"] = "Такой Алиас уже существует.";		}	}else{		$data["message"] = "Заполните поле 'Алиас'";	}		