tr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);	if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}	$data['real_item_id'] = $item_id;	/*IMG GALLERY SET*/	$file_path = "../../../../split/files/news_gal/";	$filename = "gallery";	//echo '<pre>'; print_r($item_id); echo '</pre>'; exit();	if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0 && $item_id)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"gall_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_page_news_files (`ref`, `file`) VALUES ('$item_id', '$file_upload')";				$ah->rs($query,0,1);				chmod($file_path.$file_upload,0777);							}		}	}		$data['message'] = "Новость успешно создана. ID =  $item_id";	$data['status'] = "success";	