<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$alias = $_POST['alias'];	$layouts = $_POST['layout_id'];		$cardUpd = array(					'name'			=> $_POST['name'],					'caption'			=> $_POST['caption'],					'sub_title'			=> $_POST['sub_title'],					'alias'			=> $alias,					'content'			=> $_POST['content'],					'block'			=> $_POST['block'][0],					'target'			=> $_POST['target'][0],					'meta_title'		=> $_POST['meta_title'],					'meta_desc'		=> $_POST['meta_desc'],					'meta_keys'		=> $_POST['meta_keys']										);	if ($layouts) {		$ids = implode(',', $layouts);		$cardUpd['layouts'] = $ids;	}		$query = "SELECT M.id FROM `osc_page_program_items` AS M WHERE M.alias = '$alias' LIMIT 1";	$check_alias = $ah->rs($query);	if ($check_alias) {		$data['status'] = "failed";		$data['message'] = "Программа с алиасом <<b>$alias</b>> уже существует. Алиас должен быть уникальным.";		$data['choice'] = 3;		echo json_encode($data); exit();	}	// File upload filename	$file_path = "../../../../split/files/programs/";	// PREVIEW ------------------	$im_1_filename = "preview";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "prog_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>400,				'resize_h'		=>300,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}	// FILENAME ------------------	$im_2_filename = "filename";	$im_2 		= false;	$im_2_name 	= 5;	$im_2_pre 	= "prog_";		if(isset($_FILES[$im_2_filename]) && $_FILES[$im_2_filename]['size'] > 0)	{		$im_2 		= true;	}		if($im_2)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_2_name,				'pre'			=>$im_2_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_2_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>400,				'resize_h'		=>300,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_2_filename] = $file_update;		}	}		// Create main table item		$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);	if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}	$data['real_item_id'] = $item_id;			$data['message'] = "Программа успешно создана. ID =  $item_id";	$data['status'] = "success";	