<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'name'			=> $_POST['name'],					'house_area'	=> (double)$_POST['house_area'],					'sector_area'	=> (double)$_POST['sector_area'],					'price'			=> (double)$_POST['price'],					'type'			=> $_POST['type'],					'block'			=> $_POST['block'][0],	);	$file_path = "../../../../split/files/townhouses/";	$im_1_filename = "filename";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "thbpr_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}	// Create main table item		$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);	if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}	$data['real_item_id'] = $item_id;	/*IMG GALLERY SET*/		$filename = "layouts";	//echo '<pre>'; print_r($item_id); echo '</pre>'; exit();	if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0 && $item_id)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"thbl_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_sys_th_boxes_items (`ref`, `filename`, `type`) VALUES ('$item_id', '$file_upload', '1')";				$ah->rs($query,0,1);				chmod($file_path.$file_upload,0777);			}		}	}	$filename = "photos";	if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0 && $item_id)	{						$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"thbp_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_sys_th_boxes_items (`ref`, `filename`, `type`) VALUES ('$item_id', '$file_upload', '2')";				$ah->rs($query,0,1);				chmod($file_path.$file_upload,0777);			}		}	}		$data['message'] = "Таунхаус успешно создан. ID =  $item_id";	$data['status'] = "success";	