<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'name'			=> $_POST['name'],					'details'			=> $_POST['details'],					'block'			=> $_POST['block'][0],					'order_id'		=> (int)$_POST['order_id']	);	if (isset($_POST['event_id']) && $_POST['event_id']) {		$cardUpd['event_id'] = $_POST['event_id'];	}	// File upload filename	$file_path = "../../../../split/files/townhouses/";	// PREVIEW ------------------	$im_1_filename = "preview";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "thb_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}		// Create main table item		$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);	if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}	$data['real_item_id'] = $item_id;	/*	$layouts_id = (isset($_POST['layout_id']) && $_POST['layout_id'] ? $_POST['layout_id'] : array());	if ($layouts_id) {		foreach ($layouts_id as $key => $value) {			$q = "UPDATE `osc_sys_townhouses_layouts` SET `townhouse_id` = '$item_id' WHERE `id` = '$value'";			$ah->rs($q,0,1);		}	}	*/		$data['message'] = "Секция таунхауса успешно создана. ID =  $item_id";	$data['status'] = "success";	