<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_about_page";		$item_id = 1;			$cardUpd = array(					'caption'	=> $_POST['caption'],					'dev_desc'	=> $_POST['dev_desc'],					'projects_caption'	=> $_POST['projects_caption'],					'projects_desc'	=> $_POST['projects_desc'],					'stat'	=> $_POST['stat'],										'meta_title'	=> $_POST['meta_title'],					'meta_desc'		=> $_POST['meta_desc'],					'meta_keys'		=> $_POST['meta_keys'],										'modified'	=> date("Y-m-d H:i:s", time())					);		// File upload filename	$file_path = "../../../../split/files/projects/";	// PREVIEW ------------------	$im_1_filename = "map";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "abpm_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}		$query = "UPDATE $appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query);				$data['message'] = "Страница 'О нас' успешно обновлена.";	