<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(					'caption'			=> $_POST['caption'],					'description'			=> $_POST['description'],					'cat'			=> $_POST['cat'],					'block'			=> $_POST['block'][0],										'created'		=> $_POST['created'],										'modified'	=> date("Y-m-d H:i:s", time())					);		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query);	/*IMG GALLERY SET*/	$file_path = "../../../../split/files/bp/";	$filename = "photos";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"bpf_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>500,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$now = date("Y-m-d H:i:s", time());				$query = "INSERT INTO osc_sys_bp_photos (`entry_id`, `source`, `created`, `modified`) VALUES ('$item_id', '$file_upload', '$now', '$now')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);							}		}	}		$data['status'] = "success";	$data['message'] = "Отчет #$item_id успешно сохранен.";	