<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_contacts_page";		$item_id = 1;	$cardUpd = array(					'caption'	=> $_POST['caption'],					'details'		=> $_POST['details'],					'work_from'		=> (float)$_POST['work_from'],					'work_to'		=> (float)$_POST['work_to'],					'holidays'		=> $_POST['holidays'],					'addr_desc'		=> $_POST['addr_desc'],					'phone1'		=> $_POST['phone1'],					'phone2'		=> $_POST['phone2'],					'meta_title'	=> $_POST['meta_title'],					'meta_desc'		=> $_POST['meta_desc'],					'meta_keys'		=> $_POST['meta_keys'],										'modified'	=> date("Y-m-d H:i:s", time())					);	// File upload filename	$file_path = "../../../../split/files/images/";	$im_1_filename = "filename";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "cimg_";	if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0){		$im_1 		= true;	}	if($im_1){		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>400,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($file_update){			$cardUpd[$im_1_filename] = $file_update;		}	}								// Update main table		$query = "UPDATE $appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query);	$data['message'] = "Страница 'Контакты' успешно обновлена.";	