<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(					'layout_name'			=> $_POST['layout_name'],					'house_id'			=> $_POST['house_id'],					'room_id'			=> $_POST['room_id'],					'properties'			=> $_POST['properties'],					'features'			=> $_POST['features'],					'flat_desc'			=> $_POST['flat_desc'],										'block'			=> $_POST['block'][0],					'modified' => date('Y-m-d H:i:s', time())					);	// File upload filename	$file_path = "../../../../split/files/layouts/";	$filename = "layouts";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"flyts_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload){			$now = date("Y-m-d H:i:s");			foreach($files_upload as $file_upload){				$query = "INSERT INTO `osc_sys_flats_layouts` (`filename`, `flat_id`, `created`, `modified`) VALUES ('$file_upload', '$item_id', '$now', '$now')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);			}		}	}	if ($_POST["house_id"]) {		if ($_POST["room_id"]) {			$query = "UPDATE [pre]$appTable SET ";				$cntUpd = 0;			foreach($cardUpd as $field => $itemUpd)			{				$cntUpd++;				$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");			}						$query .= " WHERE `id`=$item_id LIMIT 1";								$ah->rs($query);									$data['status'] = "success";			$data['message'] = "Квартира успешно сохранена.";		}else{			$data['message'] = "Выберите категорию.";		}	}else{		$data['message'] = "Выберите дом.";	}		