<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_home_ban";		$item_id = 1;		$cardUpd = array(						'block'			=> $_POST['block'][0],						'pan_caption'		=> $_POST['pan_caption'],						'pan_sub_caption'		=> $_POST['pan_sub_caption'],						'panorama'		=> $_POST['panorama'],						'modified' => date("Y-m-d H:i:s", time())					);	$filename = "genplan";		$file_path = "../../../../split/files/images/";	if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"hmgp_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_filename'];			unlink($file_path.$curr_filename);		}	}							// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['status'] = "success";	$data['message'] = "Секция - 'Баннер на главной' успешно сохранена.";	