<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;		$cardUpd = array(					'caption'			=> $_POST['caption'],					'sub_caption'		=> $_POST['sub_caption']					);							// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);	/*IMG GALLERY SET*/	$file_path = "../../../../split/files/home/";	$filename = "items";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"hmp_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($files_upload){			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_home_banner_items (`file`) VALUES ('$file_upload')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);							}		}	}		$data['status'] = "success";	$data['message'] = "Секция - 'Баннер на главной' успешно сохранена.";	