<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_home_env_items";		$item_id = $_POST['item_id'];		$cardUpd = array(					'caption'			=> $_POST['caption'],					'ref'			=> $_POST['ref'],					'time_walk'			=> (int)$_POST['time_walk'],					'time_car'			=> $_POST['time_car'],					'order_id'			=> (int)$_POST['order_id'],					'block'			=> $_POST['block'][0],					'modified' => date('Y-m-d H:i:s', time())					);				$query = "UPDATE `$appTable` SET ";			$cntUpd = 0;			foreach($cardUpd as $field => $itemUpd)			{				$cntUpd++;				$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");			}						$query .= " WHERE `id`=$item_id LIMIT 1";								$ah->rs($query);									$data['status'] = "success";			$data['message'] = "Элемент окружения успешно сохранен.";	