<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = `osc_home_infrasctucture`;		$item_id = $_POST['item_id'];		$cardUpd = array(					'name'			=> $_POST['name'],					'sub_caption'			=> $_POST['sub_caption'],					'alias'			=> $_POST['alias'],					'details'			=> $_POST['details'],										'block'			=> $_POST['block'][0],					'modified' => date("Y-m-d H:i:s", time())					);	// File upload filename	$file_path = "../../../../split/files/images/";	// PREVIEW ------------------	$im_1_filename = "image";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "htiim_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>800,				'resize_h'		=>500,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}	// File upload filename	$file_path = "../../../../split/files/images/";	// PREVIEW ------------------	$im_1_filename = "icon";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "htiic_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>370,				'resize_h'		=>250,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}	$alias = $_POST["alias"];	if ($alias) {		$q = "SELECT M.id FROM `osc_home_infrasctucture` AS M WHERE M.alias = '$alias' AND M.id != '$item_id' LIMIT 1";		$check_alias = $ah->rs($q);		if (!$check_alias) {			// Update main table				$query = "UPDATE `osc_home_infrasctucture` SET ";						$cntUpd = 0;			foreach($cardUpd as $field => $itemUpd)			{				$cntUpd++;				$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");			}						$query .= " WHERE `id`=$item_id LIMIT 1";			$ah->rs($query);									$data['status'] = "success";			$data['message'] = "Таб #$item_id успешно сохранен.";		}else{			$data["message"] = "Такой Алиас уже существует";		}	}else{		$data["message"] = "Заполните поле 'Алиас'.";	}							