<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;		$cardUpd = array(					'caption'			=> $_POST['caption'],					'details'		=> $_POST['details'],					'block'			=> $_POST['block'][0]					);							// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}	$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);			$data['status'] = "success";	$data['message'] = "Секция - 'Карта на главной' успешно отредактирована.";	