<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = "osc_sys_manager";		$item_id = 1;		$cardUpd = array(					'caption'	=> $_POST['caption'],					'phone'		=> $_POST['phone'],					'details'		=> $_POST['details'],					'viber'	=> $_POST['viber'][0],					'telegram'	=> $_POST['telegram'][0],					'block'	=> $_POST['block'][0],					'modified' => date("Y-m-d H:i:s", time())					);						// File upload 		$filename = "photo";		$file_path = "../../../../split/files/images/";	if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"mng_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>500,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_filename'];			unlink($file_path.$curr_filename);		}	}		// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query,0,1);		$data['status'] = "success";	$data['message'] = "Секция `Менеджер` обновлена.";	