<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(					'caption'			=> $_POST['caption'],					'details'			=> $_POST['details'],					'cat'			=> $_POST['cat'],					'block'			=> $_POST['block'][0],					'is_top'			=> $_POST['is_top'][0],						'special_text'			=> $_POST['special_text'],					'tags'			=> $_POST['tags'],					'order_id'		=> (int)$_POST['order_id'],					'meta_title'		=> $_POST['meta_title'],					'meta_desc'		=> $_POST['meta_desc'],					'meta_keys'		=> $_POST['meta_keys'],					'dateCreate'		=> $_POST['dateCreate'],										'dateModify'	=> date("Y-m-d H:i:s", time())					);	$is_top = $_POST["is_top"][0];	// File upload filename	$file_path = "../../../../split/files/news/";	// PREVIEW ------------------	$im_1_filename = "preview";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "artc_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}							// Update main table	if ($is_top == 1) {		$q = "UPDATE [pre]$appTable SET `is_top` = 0";		$ah->rs($q);	}		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query);	/*IMG GALLERY SET*/	$file_path = "../../../../split/files/news_gal/";	$filename = "gallery";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"gall_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_page_news_files (`ref`, `file`) VALUES ('$item_id', '$file_upload')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);							}		}	}		$data['status'] = "success";	$data['message'] = "Новость #$item_id успешно сохранена.";	