<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;			$cardUpd = array(															'meta_title'	=> $_POST['meta_title'],					'meta_desc'		=> $_POST['meta_desc'],					'meta_keys'		=> $_POST['meta_keys'],										'dateModify'	=> date("Y-m-d H:i:s", time())					);			$query = "UPDATE $appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query);	/*IMG GALLERY SET*/	$file_path = "../../../../split/files/building/";	$filename = "building_images";	$date_now = date("Y-m-d H:i:s", time());	if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"bp_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>150,				'resize_h'		=>100,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_page_building_items (`file`, `ref_id`, `caption`, `bp_desc`, `dateCreate`) VALUES ('$file_upload', '0', '', '', '$date_now')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);							}		}	}			$data['message'] = "Страница 'Ход строительства' успешно обновлена.";	