d as $field => $itemUpd){		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}	$query .= " WHERE `id`=$item_id LIMIT 1";	$ah->rs($query);	/*IMG GALLERY SET*/	$file_path = "../../../../split/files/content/";	$filename = "lion_gal";	if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0){				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"flg_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_page_flats_gallery (`file`, `ref_id`) VALUES ('$file_upload', '1')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);							}		}	}	$filename = "shatel_gal";	if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0){				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"flg_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($files_upload)		{			foreach($files_upload as $file_upload)			{				$query = "INSERT INTO osc_page_flats_gallery (`file`, `ref_id`) VALUES ('$file_upload', '2')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);							}		}	}		$data['message'] = "Страница 'Квартиры' успешно обновлена.";	