<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];	$alias = $_POST['alias'];	$free_layouts = $_POST['free_layout_id'];	$used_layouts = $_POST['used_layout_id'];		$cardUpd = array(					'name'			=> $_POST['name'],					'caption'			=> $_POST['caption'],					'sub_title'			=> $_POST['sub_title'],					'alias'			=> $alias,					'content'			=> $_POST['content'],					'block'			=> $_POST['block'][0],					'target'			=> $_POST['target'][0],					'meta_title'		=> $_POST['meta_title'],					'meta_desc'		=> $_POST['meta_desc'],					'meta_keys'		=> $_POST['meta_keys']										);	$q = "SELECT M.layouts FROM `osc_page_program_items` AS M WHERE M.id = '$item_id' LIMIT 1";	$curr_layouts_row = $ah->rs($q);	$curr_layouts = explode(',', $curr_layouts_row[0]['layouts']);	foreach ($used_layouts as $key => $value) {		$k = array_search($value, $curr_layouts);		unset($curr_layouts[$k]);	}	$final_l = implode(',', $curr_layouts);	$final_l .= ",";	if ($free_layouts) {		$l2 = implode(',', $free_layouts);		$final_l .= $l2;	}	$cardUpd['layouts'] = $final_l;	$query = "SELECT M.id FROM `osc_page_program_items` AS M WHERE M.alias = '$alias' AND M.id != '$item_id' LIMIT 1";	$check_alias = $ah->rs($query);	if ($check_alias) {		$data['status'] = "failed";		$data['message'] = "Программа с алиасом <<b>$alias</b>> уже существует. Алиас должен быть уникальным.";		$data['choice'] = 3;		echo json_encode($data); exit();	}	// File upload filename	$file_path = "../../../../split/files/programs/";	// PREVIEW ------------------	$im_1_filename = "preview";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "prog_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>400,				'resize_h'		=>300,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}	// FILENAME ------------------	$im_2_filename = "filename";	$im_2 		= false;	$im_2_name 	= 5;	$im_2_pre 	= "prog_";		if(isset($_FILES[$im_2_filename]) && $_FILES[$im_2_filename]['size'] > 0)	{		$im_2 		= true;	}		if($im_2)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_2_name,				'pre'			=>$im_2_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_2_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>400,				'resize_h'		=>300,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_2_filename] = $file_update;		}	}							// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query);		$data['status'] = "success";	$data['message'] = "Программа #$item_id успешно сохранена.";	