<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(					'name'			=> $_POST['name'],					'alias'			=> $_POST['alias'],					'short_name'			=> $_POST['short_name'],					'house_id'			=> $_POST['house_id'],					'caption'			=> $_POST['caption'],					'order_id'			=> (int)$_POST['order_id'],					'block'			=> $_POST['block'][0],					'modified' => date('Y-m-d H:i:s', time())					);		$alias = $_POST["alias"];	$house_id = $_POST["house_id"];	$q = "SELECT M.id FROM `osc_sys_rooms` AS M WHERE M.alias = '$alias' AND M.house_id = '$house_id' AND M.id != '$item_id' LIMIT 1";	$check_alias = $ah->rs($q,1);		if (!$check_alias) {		if ($_POST["short_name"]) {			$query = "UPDATE [pre]$appTable SET ";			$cntUpd = 0;			foreach($cardUpd as $field => $itemUpd)			{				$cntUpd++;				$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");			}						$query .= " WHERE `id`=$item_id LIMIT 1";								$ah->rs($query);									$data['status'] = "success";			$data['message'] = "Категория комнат успешно сохранена.";		}else{			$data["message"] = "Укажите название в фильтре на сайте.";		}			}else{		$data["message"] = "Такая категория уже существует по выбраному дому.";	}	