<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;		$cardUpd = array(					'caption'		=> $_POST['caption'],					'left_col'		=> $_POST['left_col'],					'right_caption'		=> $_POST['right_caption'],					'right_col'		=> $_POST['right_col'],					'left_box_caption'		=> $_POST['left_box_caption'],					'left_box_desc'		=> $_POST['left_box_desc'],					'mid_box_caption'		=> $_POST['mid_box_caption'],					'mid_box_desc'		=> $_POST['mid_box_desc'],					'right_box_caption'		=> $_POST['right_box_caption'],					'right_box_desc'		=> $_POST['right_box_desc'],					'footer_desc'		=> $_POST['footer_desc'],					'left_image_desc'		=> $_POST['left_image_desc'],					'right_image_desc'		=> $_POST['right_image_desc'],					'block'			=> $_POST['block'][0]					);						// File upload 		$filename = "left_image";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_path = "../../../../split/files/townhouses/";				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"th_ab_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_filename'];						unlink($file_path.$curr_filename);		}	}	$filename = "right_image";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_path = "../../../../split/files/townhouses/";				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"th_ab_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_filename'];						unlink($file_path.$curr_filename);		}	}		// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['status'] = "success";	$data['message'] = "Секция 'О комплексе - таунхаусы' успешно сохранена";	