<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(					'name'			=> $_POST['name'],					'house_area'	=> (double)$_POST['house_area'],					'sector_area'	=> (double)$_POST['sector_area'],					'price'			=> (double)$_POST['price'],					'type'			=> $_POST['type'],					'block'			=> $_POST['block'][0]					);	$file_path = "../../../../split/files/townhouses/";	// PREVIEW ------------------	$im_1_filename = "filename";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "thbpr_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}					// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query);	/*IMG GALLERY SET*/		$filename = "layouts";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"thbl_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload){			foreach($files_upload as $file_upload){				$query = "INSERT INTO osc_sys_th_boxes_items (`ref`, `filename`, `type`) VALUES ('$item_id', '$file_upload', '1')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);			}		}	}	$filename = "photos";		if(isset($_FILES[$filename]) && count($_FILES[$filename]) > 0)	{				$files_upload = $ah->mtvc_add_files_file_miltiple(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"thbp_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>500,				'resize_h'		=>350,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($files_upload){			foreach($files_upload as $file_upload){				$query = "INSERT INTO osc_sys_th_boxes_items (`ref`, `filename`, `type`) VALUES ('$item_id', '$file_upload', '2')";				$ah->rs($query);				chmod($file_path.$file_upload,0777);			}		}	}		$data['status'] = "success";	$data['message'] = "Таунхаус #$item_id успешно сохранен.";	