<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = `osc_th_tabs`;		$item_id = $_POST['item_id'];	$lat = isset($_POST['lat']) && $_POST['lat'] ? (float)(str_replace(',', '.', $_POST['lat'])) : 0;	$lng = isset($_POST['lng']) && $_POST['lng'] ? (float)(str_replace(',', '.', $_POST['lng'])) : 0;	$cardUpd = array(					'tab_caption'			=> $_POST['tab_caption'],					'caption'			=> $_POST['caption'],					'tab_sub_caption'			=> $_POST['tab_sub_caption'],					'details'			=> $_POST['details'],					'lat'			=> $lat,					'lng'			=> $lng,					'block'			=> $_POST['block'][0],					);	// File upload filename	$file_path = "../../../../split/files/townhouses/";	// PREVIEW ------------------	$im_1_filename = "image";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "th_t_im_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$im_1_filename,				'resize_path'	=>$file_path."crop/",				'resize_w'		=>370,				'resize_h'		=>250,				'resize_path_2'	=>"0",				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));				if($file_update)		{			$cardUpd[$im_1_filename] = $file_update;		}	}	// File upload filename	$file_path = "../../../../split/files/townhouses/";	// PREVIEW ------------------	$im_1_filename = "icon";	$im_1 		= false;	$im_1_name 	= 5;	$im_1_pre 	= "th_t_ic_";		if(isset($_FILES[$im_1_filename]) && $_FILES[$im_1_filename]['size'] > 0)	{		$im_1 		= true;	}		if($im_1)	{		$file_update = false;		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>$im_1_name,				'pre'			=>$im_1_pre,				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,